			[13] = { str = "Go NW to #COORD[37,56]# to Felstone field, kill #NPCCauldron Lord Bilemaw# for his cauldron key. Then go right click the cauldron and turn in #INTarget: Felstone Field# accept #GETReturn to Chillwind Camp#", x = 37, y = 56, zone = "Western Plaguelands" },
