			[21] = { str = "Go behind the barn and look for a Wandering Skeleton and get Dalson Outhouse Key off of him. Use this to open the outhouse back here and kill Farmer Dalson and get his Dalson Cabinet Key. Use the key on the locked cabinet upsairs in the house now at #COORD[47,49]# next to the barn and complete the quest #INLocked Away#", x = 47, y = 49, zone = "Western Plaguelands" },
			[22] = { str = "Go back to Chillwind Camp at #COORD[42,84]# and turn in #INReturn to Chillwind Camp# accept #GETTarget: Writhing Haunt#", x = 42, y = 84, zone = "Western Plaguelands" },
			[23] = { str = "Hearth to IF then fly to SW" },
