			[30] = { str = "Go back to Felstone field to the house upstairs at #COORD[38,54]# and turn in #INGood Luck Charm# accept #GETTwo Halves Become One# Go outside and look for a jabbering ghoul. You can’t miss him he carry’s a pitchfork. Keep clearing in front of the house until you find him. This will give you a good time to get those skeletal fragments", x = 38, y = 54, zone = "Western Plaguelands" },
