			[33] = { str = "In the house at #COORD[54,65]# accept #GETThe Wildlife Suffers Too pt.1# now go north of the haunt and kill the diseased wolves. Don’t go any farther east to find these just go North, maybe NW some.", x = 54, y = 65, zone = "Western Plaguelands" },
