			[35] = { str = "Go back to Chillwind Camp at #COORD[42,84]# and turn in #INReturn to Chillwind Camp# accept #GETTarget: Gahrron's Withering#", x = 42, y = 84, zone = "Western Plaguelands" },
			[36] = { str = "Go just north of the Writhing Haunt between the mountains on the map at, #COORD[52,56]# and kill diseased grizzleys on your way down to #COORD[62,58]# for #DOThe Wildlife Suffers Too pt.2#", x = 52, y = 56, zone = "Western Plaguelands" },
			[37] = { str = "Go NE to #COORD[62,58]# to Gahrron’s Withering, kill #NPCCauldron Lord Soulwrath# for his cauldron key. Then go right click the cauldron and turn in #INTarget: Gahrron's Withering# accept #GETReturn to Chillwind Camp#", x = 62, y = 58, zone = "Western Plaguelands" },
			[38] = { str = "Go back to #COORD[54,65]# and turn in #INThe Wildlife Suffers Too pt.2# accept #GETGlyphed Oaken Branch#", x = 54, y = 65, zone = "Western Plaguelands" },
			[39] = { str = "If you haven’t finished Skeletal Fragments yet go to the crypt at #COORD[54,79]# and kill the skeletons until you have 15 fragments", x = 54, y = 79, zone = "Western Plaguelands" },
			[40] = { str = "Go back to Chillwind Camp at #COORD[42,84]# and turn in #INReturn to Chillwind Camp#", x = 42, y = 84, zone = "Western Plaguelands" },
			[41] = { str = "Turn around to the guy in front of the tent, and complete the quest #INMission Accomplished!# no link for it cuz it’s not a quest again lol. Now see that running back and forth seemed out of the way but there was a huge xp boost at the end, and nice xp throughout." },
			[42] = { str = "To your right turn in #INSkeletal Fragments# SKIP the next part unless you want to start on your scholo key." },
