-----------56-56 Silithus
	--[510] = {
	[5656] = {
		title = "56-56 Silithus",
		--n = "56-56 Silithus",
		--pID = 509, nID = 511,
		--itemCount = 42,
		items = {
			[1] = { str = "56-56 Silithus" },
			[2] = { str = "Near the moonwell accept #ACCEPTThe Twilight Mystery#" },
			[3] = { str = "In front of the Inn, turn in #TURNINTaking Back Silithus# accept #ACCEPTSecuring the Supply Lines#" },
			[4] = { str = "SKIP the quest on the sign unless you get a group" },
			[5] = { str = "Make Cenarion Hold your home" },
			[6] = { str = "Upstairs accept #ACCEPTDeadly Desert Venom#" },
			[7] = { str = "There seems to be a lot of white quests because there's many AQ quests that you don’t have to worry about yet." },
			[8] = { str = "Go do #DOQUESTSecuring the Supply Lines# and #DOQUESTDeadly Desert Venom# North and NE of town all around 55,24 the dredges, scorpids, and skitterers for these 2 quests are all over this area.", x = 55, y = 24, zone = "Silithus" },
			[9] = { str = "Run up to 81,18 and turn in #TURNINWasteland# accept #ACCEPTThe Spirits of Southwind#", x = 81, y = 18, zone = "Silithus" },
			[10] = { str = "Run up NW to around 22.9 and pick up the twilight tablet fragments for #DOQUESTThe Twilight Mystery# They litter this spot", x = 22, y = 9, zone = "Silithus" },
			[11] = { str = "Hearth to Cenarion Hold" },
			[12] = { str = "Up stairs turn in #TURNINDeadly Desert Venom# accept #ACCEPTNoggle's Last Hope#" },
			[13] = { str = "In front of the Inn, turn in #TURNINSecuring the Supply Lines# accept #ACCEPTStepping Up Security#" },
			[14] = { str = "Go next to the moonwell and turn in #TURNINThe Twilight Mystery# accept #ACCEPTThe Deserter#" },
			[15] = { str = "You should be about 30-35% into level 56" },
			[16] = { str = "Go SE to 62,53 and kill the undead mobs here for #DOQUESTThe Spirits of Southwind# be careful when they die they spawn 1-2 bugs", x = 62, y = 53, zone = "Silithus" },
			[17] = { str = "Kill the pincers for #DOQUESTNoggle's Last Hope# and drudges for #DOQUESTThe Deserter# south of here around 56,58", x = 56, y = 58, zone = "Silithus" },
			[18] = { str = "Go to the cave at 67,69 and turn in #TURNINThe Deserter# accept #ACCEPTThe Twilight Lexicon#", x = 67, y = 69, zone = "Silithus" },
			[19] = { str = "You can find flayers and stalkers outside of the cave for #DOQUESTNoggle's Last Hope# they are also both around 43,72 on the other side of the hive", x = 43, y = 72, zone = "Silithus" },
			[20] = { str = "Hearth back to Cenarion Hold or die so you end up there" },
			[21] = { str = "Go upstairs and turn in for #TURNINNoggle's Last Hope# accept #ACCEPTNoggle's Lost Satchel#" },
			[22] = { str = "Outside the Inn, turn in #TURNINStepping Up Security#" },
			[23] = { str = "Go to 29,35 and kill Twilight Keeper Manya for #DOQUESTThe Twilight Lexicon# she fears a bit. She’s at the SE corner of came", x = 29, y = 35, zone = "Silithus" },
			[24] = { str = "Go to 41,41 and kill Twilight Keeper Havanuth for #DOQUESTThe Twilight Lexicon# he’s on the east side of camp, he has a nasty aoe", x = 41, y = 41, zone = "Silithus" },
			[25] = { str = "Go all the way down south to the little camp at 42,90 and go up the hill behind it, you’ll find Deathclasp, in the middle of the path on the wall is a pouch, grab it when deathclasp is in the back for #DOQUESTNoggle's Lost Satchel#", x = 42, y = 90, zone = "Silithus" },
			[26] = { str = "Head to the SW to 16,87 and kill Twilight Keeper Exeter for #DOQUESTThe Twilight Lexicon# he’s at the SW corner of camp", x = 16, y = 87, zone = "Silithus" },
			[27] = { str = "Go back to the cave at 67,69 and turn in #TURNINThe Twilight Lexicon# accept #ACCEPTA Terrible Purpose# He also offers another quest for 10 twilight texts, this is more for cen circle rep later on", x = 67, y = 69, zone = "Silithus" },
			[28] = { str = "Run up to 81,18 and turn in #TURNINThe Spirits of Southwind# accept #ACCEPTHive in the Tower#", x = 81, y = 18, zone = "Silithus" },
			[29] = { str = "Go to the top of the tower at 61,52 Be careful 3 bugs spawn when you enter. Shift right click the pod because as soon as you open it some amushers spawn. Kill at least 1 and loot it for #DOQUESTHive in the Tower#", x = 61, y = 52, zone = "Silithus" },
			[30] = { str = "Go up to 81,18 and turn in #TURNINHive in the Tower# accept #ACCEPTUmber, Archivist#", x = 81, y = 18, zone = "Silithus" },
			[31] = { str = "Hearth to Cenarion Hold" },
			[32] = { str = "." },
			[33] = { str = "Go upstairs and turn in #TURNINNoggle's Lost Satchel#" },
			[34] = { str = "Go into the long building and turn in #TURNINA Terrible Purpose#" },
			[35] = { str = "Fly to Moonglade" },
			[36] = { str = "Go to 44,35 and turn in #TURNINUmber, Archivist# accept #ACCEPTUncovering Past Secrets#", x = 44, y = 35, zone = "Moonglade" },
			[37] = { str = "Run over to 51,44 and turn in #TURNINUncovering Past Secrets#", x = 44, y = 35, zone = "Moonglade" },
			[38] = { str = "You should now be 80-85% into leveling" },
			[39] = { str = "Fly to Auberdine and sail to Menethil then fly to IF" },
			[40] = { str = "Make IF your home, then find the wandering commander dwarf and accept  #ACCEPTA Call To Arms: The Plaguelands!#" },
			[41] = { str = "Fly to Southshore" },
			[42] = { str = "Run north into Alterac Mountains and over NE to 80,34 and run into Western Plaguelands (WPL)", x = 80, y = 34, zone = "Alterac Mountains" },
		}
	},

------------56-57 Western Plaguelands
	--[511] = {
	[5657] = {
		title = "56-57 Western Plaguelands",
		--n = "56-57 Western Plaguelands",
		--pID = 510, nID = 512,
		--itemCount = 43,
		items = {
			[1] = { str = "56-57 Western Plaguelands" },
			[2] = { str = "Next to the shack accept #ACCEPTA Plague Upon Thee pt.1#" },
			[3] = { str = "Grab the FP" },
			[3] = { str = "Whenever you come back to camp here always check the vendor for the stormshroud pants recipe. It’s like 1.3g and you can easily sell it for 10 or 20g+ It’s a limited recipe that restocks 1 every hour." },
			[4] = { str = "Go to the tent and turn in #TURNINA Call To Arms: The Plaguelands!#, accept #ACCEPTClear The Way#" },
			[5] = { str = "Go to the 3 Argent Dawn people near the other tent and turn in #TURNINThe Everlook Report# and also complete #ACCEPTArgent Dawn Commission# it’s not a quest you had she just uses it to give you the trinket which you should always wear when in WPL and EPL because it gives you AD rep and allows the scourge tokens to drop, which she also has 3 new quests on those for turn ins. You most likely won’t get anything past the first quest as the rest are mainly in the instances." },
			[6] = { str = "Save the tokens until I say to hand them in" },
			[7] = { str = "Go NE to around 51,78 and grind the ghouls here until you got enough for #DOQUESTClear The Way#", x = 51, y = 78, zone = "Western Plaguelands" },
			[8] = { str = "Go back to Chillwind Camp at 42,84 and turn in #TURNINClear The Way# accept #ACCEPTAll Along the Watchtowers# and #ACCEPTThe Scourge Cauldrons#", x = 42, y = 84, zone = "Western Plaguelands" },
			[9] = { str = "Turn around and turn in #TURNINThe Scourge Cauldrons# accept #ACCEPTTarget: Felstone Field#" },
			[10] = { str = "You should now be about 5-10k from level 57" },
			[11] = { str = "Go back NE to 48,72 and go over the bridge", x = 48, y = 72, zone = "Western Plaguelands" },
			[12] = { str = "Just in the town on the left at 47,71 is the 4th tower for #DOQUESTAll Along the Watchtowers#, there are elites inside but you can stand out of LOS on the edge and mark the doors easily. 1st tower West of here at 40,71. 2nd tower NE of here at 42,66. 3rd tower NE of here at 44,63. Just behind this tower.", x = 47, y = 71, zone = "Western Plaguelands" },
			[13] = { str = "Go NW to 37,56 to Felstone field, kill #NPCCauldron Lord Bilemaw# for his cauldron key. Then go right click the cauldron and turn in #TURNINTarget: Felstone Field# accept #ACCEPTReturn to Chillwind Camp#", x = 37, y = 56, zone = "Western Plaguelands" },
			[14] = { str = "Theres 2 houses on the NE side of the field at 38,54, enter the northern house and go upstairs. Accept #ACCEPTBetter Late Than Never pt.1#", x = 38, y = 54, zone = "Western Plaguelands" },
			[15] = { str = "Go in the barn next door and grab the box on the floor and turn in #TURNINBetter Late Than Never pt.1# accept #ACCEPTBetter Late Than Never pt.2#" },
			[16] = { str = "Run back to Chillwind Camp at 42,84 and turn in #TURNINAll Along the Watchtowers#, accept #ACCEPTScholomance# SKIP the other unless you get about 10-20 people.", x = 42, y = 84, zone = "Western Plaguelands" },
			[17] = { str = "Turn in #TURNINScholomance# just behind him, accept #ACCEPTSkeletal Fragments Bones# for this will take a while and drop off any skeletons." },
			[18] = { str = "Near the FP turn in #TURNINReturn to Chillwind Camp# accept #ACCEPTTarget: Dalson's Tears#" },
			[19] = { str = "Go up to 46,52 to Dalson’Tears, kill #NPCCauldron Lord Malvinious# for his cauldron key. Then go right click the cauldron and turn in #TURNINTarget: Dalson's Tears# accept #ACCEPTReturn to Chillwind Camp#", x = 46, y = 52, zone = "Western Plaguelands" },
			[20] = { str = "Enter the barn here at 47,50 and touch the diary on the floor and complete it, another quest that isn’t a quest.", x = 47, y = 50, zone = "Western Plaguelands" },
			[21] = { str = "Go behind the barn and look for a Wandering Skeleton and get Dalson Outhouse Key off of him. Use this to open the outhouse back here and kill Farmer Dalson and get his Dalson Cabinet Key. Use the key on the locked cabinet upsairs in the house now at 47,49 next to the barn and complete the quest #TURNINLocked Away#", x = 47, y = 49, zone = "Western Plaguelands" },
			[22] = { str = "Go back to Chillwind Camp at 42,84 and turn in #TURNINReturn to Chillwind Camp# accept #ACCEPTTarget: Writhing Haunt#", x = 42, y = 84, zone = "Western Plaguelands" },
			[23] = { str = "Hearth to IF then fly to SW" },
			[24] = { str = "Go to the building at 48,30 on the 2nd floor of the cathedral area and turn in #TURNINBetter Late Than Never pt.2# accept #ACCEPTGood Natured Emma#", x = 48, y = 30, zone = "Stormwind" },
			[25] = { str = "Emma travels between the cathedral and the trade district, find her and turn in #TURNINGood Natured Emma# accept #ACCEPTGood Luck Charm#" },
			[26] = { str = "Go into the keep at 78,17 and accept #ACCEPTThe First and the Last#", x = 78, y = 17, zone = "Stormwind" },
			[27] = { str = "Go to 75,79 and turn in #TURNINThe First and the Last# accept #ACCEPTHonor the Dead# then turn it right back in, accept #ACCEPTFlint Shadowmore#", x = 75, y = 79, zone = "Stormwind" },
			[28] = { str = "Fly back to WPL" },
			[29] = { str = "In front of the house turn in #TURNINFlint Shadowmore# accept #ACCEPTThe Eastern Plagues#" },
			[30] = { str = "Go back to Felstone field to the house upstairs at 38,54 and turn in #TURNINGood Luck Charm# accept #ACCEPTTwo Halves Become One# Go outside and look for a jabbering ghoul. You can’t miss him he carry’s a pitchfork. Keep clearing in front of the house until you find him. This will give you a good time to get those skeletal fragments", x = 38, y = 54, zone = "Western Plaguelands" },
			[31] = { str = "Combine the halves and go back upstairs and turn in #TURNINTwo Halves Become One#" },
			[32] = { str = "Go SE to 53,65 to Writhing Haunt, kill #NPCCauldron Lord Razarch# for his cauldron key. Then go right click the cauldron and turn in #TURNINTarget: Writhing Haunt# accept #ACCEPTReturn to Chillwind Camp#", x = 53, y = 65, zone = "Western Plaguelands" },
			[33] = { str = "In the house at 54,65 accept #ACCEPTThe Wildlife Suffers Too pt.1# now go north of the haunt and kill the diseased wolves. Don’t go any farther east to find these just go North, maybe NW some.", x = 54, y = 65, zone = "Western Plaguelands" },
			[34] = { str = "Go back to 54,65 and turn in #TURNINThe Wildlife Suffers Too pt.1# accept #ACCEPTThe Wildlife Suffers Too pt.2#", x = 54, y = 65, zone = "Western Plaguelands" },
			[35] = { str = "Go back to Chillwind Camp at 42,84 and turn in #TURNINReturn to Chillwind Camp# accept #ACCEPTTarget: Gahrron's Withering#", x = 42, y = 84, zone = "Western Plaguelands" },
			[36] = { str = "Go just north of the Writhing Haunt between the mountains on the map at, 52,56 and kill diseased grizzleys on your way down to 62,58 for #DOQUESTThe Wildlife Suffers Too pt.2#", x = 52, y = 56, zone = "Western Plaguelands" },
			[37] = { str = "Go NE to 62,58 to Gahrron’s Withering, kill #NPCCauldron Lord Soulwrath# for his cauldron key. Then go right click the cauldron and turn in #TURNINTarget: Gahrron's Withering# accept #ACCEPTReturn to Chillwind Camp#", x = 62, y = 58, zone = "Western Plaguelands" },
			[38] = { str = "Go back to 54,65 and turn in #TURNINThe Wildlife Suffers Too pt.2# accept #ACCEPTGlyphed Oaken Branch#", x = 54, y = 65, zone = "Western Plaguelands" },
			[39] = { str = "If you haven’t finished Skeletal Fragments yet go to the crypt at 54,79 and kill the skeletons until you have 15 fragments", x = 54, y = 79, zone = "Western Plaguelands" },
			[40] = { str = "Go back to Chillwind Camp at 42,84 and turn in #TURNINReturn to Chillwind Camp#", x = 42, y = 84, zone = "Western Plaguelands" },
			[41] = { str = "Turn around to the guy in front of the tent, and complete the quest #TURNINMission Accomplished!# no link for it cuz it’s not a quest again lol. Now see that running back and forth seemed out of the way but there was a huge xp boost at the end, and nice xp throughout." },
			[42] = { str = "To your right turn in #TURNINSkeletal Fragments# SKIP the next part unless you want to start on your scholo key." },
			[43] = { str = "Run east to 70,50 and enter the Eastern Plaguelands (EPL)", x = 70, y = 50, zone = "Western Plaguelands" },
		}
	},

-----------57-58 Eastern Plaguelands
	--[512] = {
	[5758] = {
		title = "57-58 Eastern Plaguelands",
		--n = "57-58 Eastern Plaguelands",
		--pID = 511, nID = 513,
		--itemCount = 19,
		items = {
			[1] = { str = "57-58 Eastern Plaguelands" },
			[2] = { str = "Run up the west coast to 67,4 and accept the 3 quests #ACCEPTDemon Dogs# #ACCEPTBlood Tinged Skies# and #ACCEPTCarrion Grubbage#", x = 67, y = 4, zone = "Eastern Plaguelands" },
			[3] = { str = "These are annoying quests that can take a while. Anytime from now until these are done, kill any plaguehound runts, carrion worms, or plaguebats." },
			[4] = { str = "From here on down to the bigger area, kill any dogs, worms, and bats. Focus on them while you head to the next step" },
			[5] = { str = "Keep killing the dogs and bats and head over to the skeleton at 28,74 open it and grab the insignia for #DOQUESTThe Eastern Plagues# then run up a bit further to the next skeleton at 27,74 and grab the insignia, it’s practically right in front of him and you will also get the blightcaller found complete message. Now go down near the road to 28,79 and loot the last insignia off the skeleton", x = 28, y = 74, zone = "Eastern Plaguelands" },
			[6] = { str = "Focus on the dogs and bats while you head to Darrowshire at 36,90 in the broken down house. Turn in #TURNINSister Pamela# accept #ACCEPTPamela's Doll# then head towards the houses here, in any of the houses can be any of the 3 doll parts. They are random. Ghosts spawn when you get near the parts so be aware. Once you have all 3 parts combine them and go back to 36,90 and turn in #TURNINPamela's Doll# accept #ACCEPTAuntie Marlene# and #ACCEPTUncle Carlin#", x = 36, y = 90, zone = "Eastern Plaguelands" },
			[7] = { str = "Look on your map at 48,76 This lines up with that middle ridge mountain. Don’t go past this spot east until you have your plaguehound runt kills for #DOQUESTDemon Dogs# and plaguebats for #DOQUESTBlood Tinged Skies# because there is none past that point.", x = 48, y = 76, zone = "Eastern Plaguelands" },
			[8] = { str = "Don’t forget to kill the grubs along the way too for #DOQUESTCarrion Grubbage#, they’re not as important because they’re all over." },
			[9] = { str = "Kill Plaguehounds for Demon Dogs all around 56,57 this is north of the scar and south of blackwood lake. Don’t forget the grubs", x = 56, y = 57, zone = "Eastern Plaguelands" },
			[10] = { str = "Kill Frenzied Plaguehounds for #DOQUESTDemon Dogs#  all around 61,41 this is north of blackwood lake but now west of it.", x = 61, y = 41, zone = "Eastern Plaguelands" },
			[11] = { str = "Head to lights hope chapel at 81,58 if you still need grub meat for #DOQUESTCarrion Grubbage#, then kill them as you", x = 81, y = 58, zone = "Eastern Plaguelands" },
			[12] = { str = "Get the FP" },
			[13] = { str = "Turn in #TURNINUncle Carlin# accept #ACCEPTDefenders of Darrowshire#" },
			[14] = { str = "Turn around and turn in #TURNINDuke Nicholas Zverenhoff#" },
			[15] = { str = "Run SW to 79,63 and accept #ACCEPTZaeldarr the Outcast#", x = 79, y = 63, zone = "Eastern Plaguelands" },
			[16] = { str = "Fly back to EPL" },
			[17] = { str = "In front of the house turn in #TURNINThe Eastern Plagues# accept #ACCEPTThe Blightcaller Cometh#" },
			[18] = { str = "If you’re within 6,600 to level then Hearth to IF and fly to SW, if not then grind until you’re within range." },
			[19] = { str = "Go to 78,18 in the keep and turn in #TURNINThe Blightcaller Cometh# now I SKIP the next part but it’s a good quest to do at 60", x = 78, y = 18, zone = "Eastern Plaguelands" },
			[18] = { str = "Go get new skills" },
			[19] = { str = "Fly back to WPL" },
		}
	},

-----------58-58 Western Plaguelands
	--[513] = {
	[5857] = {
		title = "58-58 Western Plaguelands",
		--n = "58-58 Western Plaguelands",
		--pID = 512, nID = 514,
		--itemCount = 9,
		items = {
			[1] = { str = "58-58 Western Plaguelands" },
			[2] = { str = "Go NE to the house at 49,78 and go upstairs. Turn in #TURNINPamela's Doll# accept #ACCEPTA Strange Historian#", x = 49, y = 78, zone = "Western Plaguelands" },
			[3] = { str = "Just north of the house at 49,76 you’ll see an off color gravestone. Loot it and get the wedding ring for #DOQUESTA Strange Historian#", x = 49, y = 76, zone = "Western Plaguelands" },
			[4] = { str = "Go in the west entrance to Andorhal at 39,71and you should see the only full building, looks like an Inn at 39,68. Go upstairs and turn in #TURNINA Strange Historian# accept #ACCEPTThe Annals of Darrowshire# also accept #ACCEPTA Matter of Time#", x = 39, y = 68, zone = "Western Plaguelands" },
			[5] = { str = "Now look all around the city here for busted up silo’s with a blue light coming out of them. Use the silo horn to summon the worms, it spawns 1-3 mobs for #DOQUESTA Matter of Time#. Once you kill the last one they have a tendency to spawn another. They’re more on the East side of town" },
			[6] = { str = "Go to the Town Hall near all the mobs in the middle of town, you can sneak in from the SW side. Grab the books on the floor until you get the #ACCEPTThe Annals of Darrowshire# Some will spawn mobs" },
			[7] = { str = "Go back to the Inn at 39,68 and to the top floor, turn in #TURNINA Matter of Time# accept #ACCEPTCounting Out Time# also turn in #TURNINThe Annals of Darrowshire# accept #ACCEPTBrother Carlin#", x = 39, y = 68, zone = "Western Plaguelands" },
			[8] = { str = "Go outside and look around the busted houses for little metal lunchboxes for #DOQUESTCounting Out Time#" },
			[9] = { str = "Go back to the Inn at 39,68 and turn in #TURNINCounting Out Time# you can now repeat this quest for a dampener. This is used vs the 61 elite", x = 39, y = 68, zone = "Western Plaguelands" },
		}
	},

-----------58-58 Eastern Plaguelands
	--[514] = {
	[5858] = {
		title = "58-58 Eastern Plaguelands",
		--n = "58-58 Eastern Plaguelands",
		--pID = 513, nID = 515,
		--itemCount = 18,
		items = {
			[1] = { str = "58-58 Eastern Plaguelands" },
			[2] = { str = "Run out of here and into EPL up to the NW at 7,43 and turn in the 3 quests #TURNINDemon Dogs# #TURNINBlood Tinged Skies# and #TURNINCarrion Grubbage# accept #ACCEPTRedemption# then listen to him and turn it back in. Then accept #ACCEPTOf Forgotten Memories#", x = 7, y = 43, zone = "Eastern Plaguelands" },
			[3] = { str = "Go to the Undercroft at 28,86 and run behind it. Touch the dirt pile to summon Mercutio and 3 guards. Easiest way to do this is to kite him away from his buddies or get a group. Loot his body for the hammer for #DOQUESTOf Forgotten Memories#", x = 28, y = 86, zone = "Eastern Plaguelands" },
			[4] = { str = "Now go to the bottom of the crypt and kill the very big troll for #DOQUESTZaeldarr the Outcast# Only grab the scroll on the floor if u want" },
			[5] = { str = "Go back to 7,43 and turn in #TURNINOf Forgotten Memories# accept #ACCEPTOf Lost Honor#", x = 7, y = 43, zone = "Eastern Plaguelands" },
			[6] = { str = "Either Run to Light’s Hope Chapel or back to Chillwind and fly there" },
			[7] = { str = "Turn in #TURNINBrother Carlin# accept #ACCEPTVillains of Darrowshire#" },
			[8] = { str = "Go down to 79,63 and turn in #TURNINZaeldarr the Outcast#", x = 79, y = 63, zone = "Eastern Plaguelands" },
			[9] = { str = "Go down just North of Corin’s Crossing to 53,65 and grab the sword for #DOQUESTVillains of Darrowshire# It’s down in the scar. Don’t forget to do some of #DOQUESTDefenders of Darrowshire# at Corin’s Just get what you can", x = 53, y = 65, zone = "Eastern Plaguelands" },
			[10] = { str = "Go north to Blackwood Lake at 51,49 and grab the skull under the water for #DOQUESTVillains of Darrowshire#", x = 51, y = 49, zone = "Eastern Plaguelands" },
			[11] = { str = "Go NE to around 65,41 and kill the zombies for part of #DOQUESTDefenders of Darrowshire# there are quite a lot here you could probably finish", x = 65, y = 41, zone = "Eastern Plaguelands" },
			[12] = { str = "Go to the lake at 71,33 and grab the flag next to the cage, it’s hard to see and is laying flat for #DOQUESTOf Lost Honor#", x = 71, y = 33, zone = "Eastern Plaguelands" },
			[13] = { str = "Go NW to the tower at 56,24 and turn in #TURNINTroubled Spirits of Kel'Theril# SKIP the next part", x = 56, y = 24, zone = "Eastern Plaguelands" },
			[14] = { str = "Run West to 45,34 and loot the termite mounds here for #DOQUESTA Plague Upon Thee pt.1# until you get 100 termites, also kill ghouls to finish up #DOQUESTDefenders of Darrowshire#", x = 45, y = 34, zone = "Eastern Plaguelands" },
			[15] = { str = "Run to Lights Hope Chapel now" },
			[16] = { str = "Go by the tent and turn in #TURNIN“Defenders of Darrowshire”# and #TURNIN“Villains of Darrowshire”#" },
