			[5] = { str = "Go to 51,28 and turn in #TURNIN“Unfinished Business pt.1”# accept #ACCEPT“Unfinished Business pt.2”#", x = 51, y = 28, zone = "Western Plaguelands" },
			[6] = { str = "Go to 57,36 and kill Huntsman Radly for #DOQUEST“Unfinished Business pt.2”# then north to the tower at 55,23 and kill Cavalier Durgen at the top", x = 57, y = 36, zone = "Western Plaguelands" },
			[7] = { str = "Go straight out of the tower over the mountains to 51,28 and turn in #TURNIN“Unfinished Business pt.2”# doing the 3rd part #DOQUEST“Unfinished Business pt.3”# is optional. Some classes can do it easier than others. You have to run north to hearthglen and run up into the big tower in the center of town at 45,18 and look over the edge until it says complete.", x = 45, y = 18, zone = "Western Plaguelands" },
			[8] = { str = "Run back straight south over the mountains to 51,28 and turn in #TURNIN“Unfinished Business pt.3”#", x = 51, y = 28, zone = "Western Plaguelands" },
			[9] = { str = "Run into EPL and up to 7,43 and turn in #TURNIN“Of Lost Honor”# accept #ACCEPT“Of Love and Family pt.1”#", x = 7, y = 43, zone = "Eastern Plaguelands" },
			[10] = { str = "Go back to WPL and run/swim to the island Scholo is on at 65,75 and turn in #TURNIN“Of Love and Family pt.1”# SKIP the rest until you do strat", x = 65, y = 75, zone = "Western Plaguelands" },
