			[13] = { str = "Now you want to go up to andorhal and just grind until you’re friendly, once you’re friendly go back to Chillwind Camp and turn in all your scourge tokens. This allows you to buy mana biscuits which restore mana and health and more than food can. If you still need to level then go grind on mobs in andorhal again until you’re 8-8.5k from level 59" },
			[14] = { str = "Once you’re 8k-8.5k from leveling, fly to Hinterlands and run all the way NE to the waterfall at 80,46 and jump off. Under the water loot the chest and finish #DOQUEST“Cortello’s Riddle pt.3”#", x = 80, y = 46, zone = "The Hinterlands" },
			[15] = { str = "Hearth to IF" },
			[16] = { str = "Go to the throne room at 43,52 and accept #ACCEPT“An Earnest Proposition”# (pick your class from the list) You have to have your classes tier 0 bracers in order to do this quest. You can buy it on AH", x = 43, y = 52, zone = "Ironforge" },
			[17] = { str = "Fly to Menethil and sail to Auberdine, fly to Darnassus" },
			[18] = { str = "Go to 35,8 on the 2nd floor and turn in #TURNIN“Glyphed Oaken Branch”#", x = 35, y = 8, zone = "Darnassus" },
